<style lang="scss" scoped>
.header {
  &__logo {
    display: flex;
    width: 25rem;
    color: var(--main-text);
    @include w767 {
      width: 20rem;
    }
    @include hover {
      &:hover #logo-bg,
      &:focus-visible #logo-bg {
        fill: var(--accent-logo);
      }
    }
    @include active {
      &:active #logo-bg {
        fill: var(--accent-logo);
      }
    }
    svg {
      display: block;
      width: 100%;
      height: 100%;
      fill: currentColor;
      object-fit: cover;
      #logo-bg {
        transition: fill 0.2s;
        fill: var(--main-bg);
      }
    }
  }
}
</style>
